const replaceSlideNumber = (objects, slideNumber) => {
  return objects.map(obj => {
    if (obj.type === 'textbox' && obj.text?.toLowerCase() === 'slidenumber') {
      obj.text = slideNumber.toString();
    }

    // If the object is a group, handle its nested objects
    if (obj.type === 'group' && Array.isArray(obj.objects)) {
      obj.objects = replaceSlideNumber(obj.objects, slideNumber);
    }

    return obj;
  });
};

const setTitle = (firstSlideTitle, slide_type, parsedTemplate) => {
  if (!firstSlideTitle && slide_type === 'title') {
    const titleBox = parsedTemplate.objects.find(
      obj => obj.type === 'textbox' && obj.text !== ''
    );
    if (titleBox) return titleBox.text;
  }
  return firstSlideTitle;
};

const setMainSlides = (
  baseTemplate,
  slide_type,
  selectedSlide,
  index,
  dualSlides = true
) => {
  if (slide_type === 'main' || slide_type === 'synopsis') {
    selectedSlide =
      index !== 4 && dualSlides
        ? baseTemplate.template.find(
            t =>
              (t.slide_type === 'main' || t.slide_type === 'synopsis') &&
              t.slide_id === 5
          )
        : baseTemplate.template.find(
            t =>
              (t.slide_type === 'main' || t.slide_type === 'synopsis') &&
              t.slide_id === 4
          );
  } else {
    selectedSlide = baseTemplate.template.find(
      t => t.slide_type === slide_type
    );
  }
  return selectedSlide;
};

const updateObjects = (objects, firstSlideTitle, title, image, text) =>
  objects.map(obj => {
    if (obj.type === 'image') obj.src = image || obj.src || '';
    if (obj.type === 'textbox') {
      if (obj.text === '') obj.text = text || '';
      else if (obj.text?.toLowerCase() === 'title')
        obj.text = firstSlideTitle || title || '';
    }
    if (obj.type === 'group' && Array.isArray(obj.objects))
      obj.objects = updateObjects(
        obj.objects,
        firstSlideTitle,
        title,
        image,
        text
      );
    return obj;
  });

