          parsedTemplate.objects = updateObjects(
            parsedTemplate.objects,
            firstSlideTitle,
            title,
            image,
            text
          );

          parsedTemplate.objects = replaceSlideNumber(
            parsedTemplate.objects,
            index
          );

          if (!firstSlideTitle && slide_type === 'title') {
            firstSlideTitle = setTitle(
              firstSlideTitle,
              slide_type,
              parsedTemplate
            );
          }

          return {
            slide_id: idx,
            slide_type,
            visual_prompt,
            json: JSON.stringify(parsedTemplate),
            hasContent: true,
          };
